mapper.insert:
INSERT INTO NOTICE
(
	  NOTICE_NO
	, TITLE
	, CONTENT
	, WRITER
	, ENROLL_DATE
)
VALUES
(
	  NOTICE_NO_SEQ.NEXTVAL
	, #{title}
	, #{content}
	, #{writer}
	, SYSDATE
)

mapper.update:
UPDATE NOTICE
SET 
  CONTENT = #{content}
, TITLE = #{title}
WHERE NOTICE_NO = #{noticeNo}

mapper.delete:
DELETE FROM NOTICE
WHERE NOTICE_NO IN

#{x}

mapper.select:
SELECT * FROM
(
	SELECT ROWNUM RN, T.* 
	FROM
	(
		SELECT *
		FROM NOTICE
	) T
)
WHERE RN BETWEEN #{startRow} AND #{endRow}
