mapper.insert:
INSERT INTO PAYROLL
(
	  EMP_NO
	, BASIC_SALARY
	, OVERTIME_ALLOWANCE
	, HOLIDAY_ALLOWANCE
	, LONG_SERVICE_ALLOWANCE
	, POSITION_ALLOWANCE
	, ANNUAL_LEAVE_ALLOWANCE
	, MEAL_EXPENSES
	, TRANSPORTATION_EXPENSES
	, BONUS
	, SPECIAL_ALLOWANCE
	, TOTAL_SALARY
	, FOUR_MAJOR_INSURANCES
	, TAX
	, NET_SALARY
	, PAYROLL_BANK
	, PAYROLL_ACCOUNT
	, PAYROLL_DATE
)
VALUES
(
	  #{empNo}
	, #{basicSalary}
	, #{overtimeAllowance}
	, #{holidayAllowance}
	, #{longServiceAllowance}
	, #{positionAllowance}
	, #{annualLeaveAllowance}
	, #{mealExpenses}
	, #{transportationExpenses}
	, #{bonus}
	, #{specialAllowance}
	, #{totalSalary}
	, #{fourMajorInsurances}
	, #{tax}
	, #{netSalary}
	, #{payrollBank}
	, #{payrollAccount}
	, #{payrollDate}
)

mapper.update:
UPDATE SALARY
SET 
  REGULAR_HOURLY_WAGE = #{regularHourlyWage}
, LONG_SERVICE_DAY = #{longServiceDay}
, LONG_SERVICE_ALLOWANCE = #{longServiceAllowance}
, POSITION_ALLOWANCE = #{positionAllowance}
, MEAL_EXPENSES = #{mealExpenses}
, TRANSPORTATION_EXPENSES = #{transportationExpenses}

WHERE EMP_NO = 101
