mapper.insert:
INSERT INTO PAYROLL
(
	  EMP_NO
	, BASIC_SALARY
	, OVERTIME_ALLOWANCE
	, HOLIDAY_ALLOWANCE
	, LONG_SERVICE_ALLOWANCE

	, ANNUAL_LEAVE_ALLOWANCE
	, MEAL_EXPENSES
	, TRANSPORTATION_EXPENSES
	, BONUS
	, SPECIAL_ALLOWANCE
	, TOTAL_SALARY
	, FOUR_MAJOR_INSURANCES
	, TAX
	, NET_SALARY
	, PAYROLL_BANK
	, PAYROLL_ACCOUNT
	, PAYROLL_DATE
)
VALUES
(
	  #{empNo}
	, #{basicSalary} * 20
	, #{overtimeAllowance}
	, #{holidayAllowance}
	, #{longServiceAllowance}

	, #{annualLeaveAllowance}
	, #{mealExpenses}
	, #{transportationExpenses}
	, #{bonus}
	, #{specialAllowance}
	, #{totalSalary}
	, #{fourMajorInsurances}
	, #{tax}
	, #{netSalary}
	, #{payrollBank}
	, #{payrollAccount}
	, #{payrollDate}
)
